const game = command.options.find(i => i.name === 'игра')
const description = command.options.find(i => i.name === 'детали')

if (!member.voice?.channelId) {
  await reply({
    content: `<@!${member.user.id}>, необходимо подключиться к голосовому каналу`,
    ephemeral: true
  })

  return
}

await reply({
  embeds: [
    {
      author: {
        icon_url: member.user.avatar,
        name: member.user.username
      },
      description: description.value,
      fields: [
        {
          name: 'Название игры',
          value: game.value,
          inline: true
        },
        {
          name: 'Голосовой канал',
          value: `<#${member.voice.channelId}>`,
          inline: true
        }
      ]
    }
  ]
})
